//   padding: 0.25em 1em;
//   border: 2px solid palevioletred;
//   border-radius: 3px;
// `;

	toggleButton = () => {
		const showIt = this.state.displayCard;
		this.setState({ displayCard: !showIt})

	};

	return(
	<div className="container" onClick={props.toggleButton}>
	    <Button variant="info" size="lg" block>Wallet</Button>
	    <div className="container walletSetting">
	  		<Button variant="outline-warning">+</Button>
	  		<Button variant="outline-secondary">edit</Button>
	   		<Button variant="outline-danger">-</Button>
	   	</div>
  	</div>
);
}




export default Wallet;
