import React , {Component} from 'react';
import Wallet from './wallet';
import Navbar from './navbar.js';
import WalletCard from './walletCard.js';
import WalletInfo from './walletInfo.js';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';



class App extends Component {
    state = {
        displayCard : false
    };

    toggleButton = () => {
        const showIt = this.state.displayCard;
        this.setState({ displayCard: !showIt})

    };
    render() {
        if(this.state.displayCard){
            console.log('haloooo')
        }
        else {console.log('pa chera');}
        return(
        <div className="App">
            <Navbar></Navbar>
            <Wallet onClick={this.props.toggleButton}></Wallet>
            <button onClick={this.toggleButton}>beinam</button>
            {/*<button onClick={this.toggleButton}/>*/}
            <WalletInfo title={"transaction"}></WalletInfo>
            <WalletInfo title={"current balance"}></WalletInfo>
            <WalletInfo title={"total period"}></WalletInfo>
            <WalletCard></WalletCard>
        </div>
        );
    }
};

export default App;
